import React from 'react'

import '../App.css';






class UserGithub extends React.Component{

  constructor(props){
    super(props)
    this.state = {
      user : null
  
    }
  }

  


  


  async componentDidMount(){
    const responsebd = await fetch('http://localhost:3000/users/soufianlabed')

    if(responsebd.statusText == 'Not Found' ){
      console.log("JE SUIS ICI")
    }
    
    const response = await fetch('https://api.github.com/users/SoufianLABED')
    const data = await response.json()
    let tab = []
    tab.push(data)
    this.setState({user : tab})
    //console.log(response.statusText)
  }


  
  

  render(){

   return (
        <div >

        
      
        <div className="row" style={{width:"100%"}}>

        {this.state.user ? (
          <div className="row">
              
            {this.state.user.map(user => (

              user.login
            ))}
          </div>
        ) : (
         <div style={{marginLeft:"auto",marginRight:"auto",height:"100vh"}}>
                Y A PAS DE DATA
         </div>
        )}
        </div>
        </div>
      
    );
  }






  }
export default UserGithub;
