import '../App.css';


class UserGithub extends React.Component{

  constructor(props){
    super(props)
    this.state = {
      user : "a",
     
    
    }
  }



  async componentDidMount(){
    const response = await fetch('https://api.github.com/users/SoufianLabed')
    const data = await response.json()
    this.setState({user: data})
    console.log(data)
 
  }



  render(){

   return (
        <div>
         {this.state.user.map(user => (
             <div>{user.login}</div>  
            ))}
              
        </div>
        
      
    );
  }






  }
export default UserGithub;
