import React from 'react'
import UserGithub from './Component/UserGithub'
import Button from '@material-ui/core/Button';
import './HomePage.css'

import { HashRouter as Router, Route, Switch,Link } from 'react-router-dom';



class HomePage extends React.Component{
    
  constructor(props){
    super(props)
    this.state = {
      nom : ''
    }

    this.handleChange = this.handleChange.bind(this)
    this.test = this.test.bind(this)
  }

  handleChange(e){
    this.setState({
      nom : e.target.value
    })

    console.log(e)
  }

  test(){
    window.location.href = `http://localhost:3000/#/users/${this.state.nom}`
  }


 
  render(){

    

    return (
      
      <div className='main'>
        
        <label htmlFor="nom">Nom de user</label>
        <input  type="texte"  id="username" value={this.state.nom} onChange={this.handleChange} />

{/*
        <Button
        variant="contained"
        color="primary">
          <Link className="link" to={`/users/${this.state.nom}`}>Click to display</Link>
        </Button>*/}

        <Button
        variant="contained"
        color="primary"
        onClick={this.test}>
          Click to display
        </Button>

        
      
        </div>
      
    );
  }






  }
export default HomePage;
