import React from 'react'

import '../App.css';






class UserGithub extends React.Component{

  constructor(props){
    super(props)
    this.state = {
      pokemon : null,
      search:''
    
    }
  }

  handleChange(e){
    this.setState({
      search: e.target.value
    })
  }


  


  async componentDidMount(){
    const response = await fetch('http://localhost:4242/')
    const data = await response.json()
    this.setState({pokemon : data})
    console.log(data)
  }


  
  

  render(){
   const librairies = this.state.pokemon
   
   let libData = this.state.pokemon;
   const searchKey = this.state.search.trim().toLocaleLowerCase();
   if(searchKey && searchKey.length > 0){
     libData = librairies.filter(i =>{
       return i.nom.toLowerCase().match(searchKey)
     })
   }
   return (
        <div >

        
        <SearchBox/>
        <div className="row" style={{width:"100%"}}>

        {this.state.pokemon ? (
          <div className="row">
              
            {this.state.pokemon.map(pokemon => (

              
           
            <CardPokemon key={pokemon.id} name={pokemon.nom} url={"https://assets.pokemon.com/assets/cms2/img/pokedex/detail/"+pokemon.numero+".png"} ide={pokemon.id} num={pokemon.numero}/>
            
            ))}
          </div>
        ) : (
         <div style={{marginLeft:"auto",marginRight:"auto",height:"100vh"}}>
           <CircularProgress/>
         </div>
        )}
        </div>
        </div>
      
    );
  }






  }
export default UserGithub;
