import React from 'react';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';

import '../App.css';




const StyledTableCell = withStyles((theme) => ({
  head: {
    backgroundColor: theme.palette.common.black,
    color: theme.palette.common.white,
    
  },
  body: {
    fontSize: 18,
  },
}))(TableCell);

const StyledTableRow = withStyles((theme) => ({
  root: {
    '&:nth-of-type(odd)': {
      backgroundColor: theme.palette.action.hover,
    },
  },
}))(TableRow);

function createData(name, calories, fat, carbs, protein) {
  return { name, calories, fat, carbs, protein };
}


const useStyles = makeStyles({
  table: {
    minWidth: 0,
  },
});




class UserGithub extends React.Component{

  constructor(props){
    super(props)
    this.state = {
      user : null,
      username : this.props.match.params.username
  
    }
  }

  


  


  async componentDidMount(){


 /*
    const response = await fetch('https://api.github.com/users/SoufianLabed')
    const data = await response.json()

    //const key = Object.keys(data).length
    //console.log("Key : "+key)
    
    let tab = []
    tab.push(data)
    this.setState({user : tab})
  //  console.log(data)*/

  
  const responsedb = await fetch(`http://localhost:4242/users/${this.state.username}`)
  const data2 = await responsedb.json()
  console.log(data2.length)

  if(data2.length == 0){

    console.log("x")
    const responseApi = await fetch(`http://api.github.com/users/${this.state.username}`)
    
    const dataApi = await responseApi.json()
    console.log("dataApi : "+dataApi)
    console.log("dataApi33 : "+dataApi)

    
    let tab = []
    console.log("dataApi2 : "+dataApi)
    tab.push(dataApi)
   // console.log(dataApi)
 
    this.setState({user : tab})

    const post = await fetch('http://localhost:4242/users', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(dataApi),
    })
    await post.json()
    
    
    
  

  }else{
    
    let tab = []
    tab.push(data2)
    console.log(data2)
    this.setState({user : data2})
  
  }

  }


  
  

  render(){

   return (
        <div >

        
      
        <div className="row" style={{width:"100%"}}>

        {this.state.user ? (
          <div className="row">
              
            {this.state.user.map(user => (

              
    
          <TableContainer  component={Paper} style={{borderRadius:"4px",marginLeft:"30px"}} >
          <Table className={classes.table} aria-label="customized table">
            <TableBody >

                <StyledTableRow style={{backgroundColor:"#706C6C"}} >
                  <StyledTableCell style={{color:"white",borderColor:"#222222",fontWeight:"bold"}} align="left">Nom</StyledTableCell>
                  <StyledTableCell style={{color:"white",borderColor:"#222222"}} align="left">{this.state.username}</StyledTableCell>
                </StyledTableRow>

              
                <StyledTableRow style={{backgroundColor:"#222222"}}>
                  <StyledTableCell style={{color:"white",borderColor:"#222222",fontWeight:"bold"}} align="left">{this.state.username}</StyledTableCell>
                  <StyledTableCell style={{color:"white",borderColor:"#222222"}} align="left">{this.state.username}</StyledTableCell>
                </StyledTableRow>
{this.state.username}
                <StyledTableRow style={{backgroundColor:"#706C6C"}}>
                  <StyledTableCell style={{color:"white",borderColor:"#222222",fontWeight:"bold"}} align="left">{this.state.username}</StyledTableCell>
                  <StyledTableCell style={{color:"white",borderColor:"#222222"}} align="left">{this.state.username}</StyledTableCell>
                </StyledTableRow>

                <StyledTableRow style={{backgroundColor:"#222222"}}>
                  <StyledTableCell style={{color:"white",borderColor:"#222222",fontWeight:"bold"}} align="left">{this.state.username}</StyledTableCell>
                  <StyledTableCell style={{color:"white",borderColor:"#222222"}} align="left">{this.state.username}</StyledTableCell>
                </StyledTableRow>

                <StyledTableRow style={{backgroundColor:"#706C6C",color:"white"}}>
                  <StyledTableCell style={{color:"white",borderColor:"#222222",fontWeight:"bold"}} align="left">{this.state.username}</StyledTableCell>
                  <StyledTableCell style={{color:"white",borderColor:"#222222"}} align="left">{this.state.username} </StyledTableCell>
                </StyledTableRow>


                <StyledTableRow style={{backgroundColor:"#222222",color:"white"}}>
                  <StyledTableCell style={{color:"white",borderColor:"#222222",fontWeight:"bold"}} align="left">{this.state.username}</StyledTableCell>
                  <StyledTableCell style={{color:"white",borderColor:"#222222"}} align="left">{this.state.username}</StyledTableCell>
                </StyledTableRow>

                <StyledTableRow style={{backgroundColor:"#706C6C",color:"white"}}>
                  <StyledTableCell style={{color:"white",borderColor:"#222222",fontWeight:"bold"}} align="left">{this.state.username}</StyledTableCell>
                  <StyledTableCell style={{color:"white",borderColor:"#222222"}} align="left">{this.state.username}</StyledTableCell>
                </StyledTableRow>

           
            </TableBody>
          </Table>
        </TableContainer>
    
            ))}
          </div>
        ) : (
         <div style={{marginLeft:"auto",marginRight:"auto",height:"100vh"}}>
                Y A PAS DE DATA
         </div>
        )}
        </div>
        </div>
      
    );
  }






  }
export default UserGithub;
