import React from 'react'

import '../App.css';


class UserGithub extends React.Component{

  constructor(props){
    super(props)
    this.state = {
      user : null,
     
    
    }
  }



  async componentDidMount(){
    const response = await fetch('https://api.github.com/users/SoufianLabed')
    const data = await response.json()
    this.setState({user : data})
    console.log(data)
  }


  
  

  render(){

   return (
        <div >
           {this.state.pokemon.map(pokemon => (

              
           
        <CardPokemon key={pokemon.id} name={pokemon.nom} url={"https://assets.pokemon.com/assets/cms2/img/pokedex/detail/"+pokemon.numero+".png"} ide={pokemon.id} num={pokemon.numero}/>

        ))}
        </div>
        
      
    );
  }






  }
export default UserGithub;
